<?php $page_title = 'ÙˆØ³Ø§Ø¦Ù„ Ø§Ù„ØªÙˆØ§ØµÙ„ Ø§Ù„Ø§Ø¬ØªÙ…Ø§Ø¹ÙŠ'; require '_header.php';

// â”€â”€ Handle POST actions â”€â”€
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $action = $_POST['action'] ?? '';
    $socials = load_json('social.json');

    if ($action === 'update') {
        // Update all URLs + enabled status
        foreach ($socials as &$s) {
            $s['url'] = trim($_POST['url_' . $s['id']] ?? '');
            $s['enabled'] = isset($_POST['enabled_' . $s['id']]);
        }
        unset($s);
        save_json('social.json', $socials);
        echo '<script>localStorage.setItem("flash","ØªÙ… Ø­ÙØ¸ Ø§Ù„ØªØ¹Ø¯ÙŠÙ„Ø§Øª Ø¨Ù†Ø¬Ø§Ø­ âœ“")</script>';
    }

    if ($action === 'add') {
        $newId = preg_replace('/[^a-z0-9_-]/', '', strtolower(trim($_POST['new_id'] ?? '')));
        $newName = trim($_POST['new_name'] ?? '');
        $newUrl = trim($_POST['new_url'] ?? '');
        if ($newId && $newName) {
            // Check duplicate
            $exists = false;
            foreach ($socials as $s) { if ($s['id'] === $newId) { $exists = true; break; } }
            if (!$exists) {
                $maxOrder = 0;
                foreach ($socials as $s) { if (($s['order'] ?? 0) > $maxOrder) $maxOrder = $s['order']; }
                $socials[] = [
                    'id' => $newId,
                    'name' => $newName,
                    'url' => $newUrl,
                    'icon' => $newId,
                    'enabled' => true,
                    'order' => $maxOrder + 1
                ];
                save_json('social.json', $socials);
                echo '<script>localStorage.setItem("flash","ØªÙ…Øª Ø¥Ø¶Ø§ÙØ© ' . esc($newName) . ' Ø¨Ù†Ø¬Ø§Ø­ âœ“")</script>';
            } else {
                echo '<script>localStorage.setItem("flash","âš ï¸ Ù‡Ø°Ø§ Ø§Ù„Ù…ÙØ¹Ø±Ù Ù…ÙˆØ¬ÙˆØ¯ Ø¨Ø§Ù„ÙØ¹Ù„")</script>';
            }
        }
    }

    if ($action === 'delete') {
        $delId = $_POST['del_id'] ?? '';
        $socials = array_values(array_filter($socials, fn($s) => $s['id'] !== $delId));
        save_json('social.json', $socials);
        echo '<script>localStorage.setItem("flash","ØªÙ… Ø§Ù„Ø­Ø°Ù Ø¨Ù†Ø¬Ø§Ø­ âœ“")</script>';
    }

    if ($action === 'reorder') {
        $order = json_decode($_POST['order_data'] ?? '[]', true);
        if (is_array($order)) {
            $map = [];
            foreach ($order as $i => $id) { $map[$id] = $i + 1; }
            foreach ($socials as &$s) {
                if (isset($map[$s['id']])) $s['order'] = $map[$s['id']];
            }
            unset($s);
            usort($socials, fn($a, $b) => ($a['order'] ?? 0) - ($b['order'] ?? 0));
            save_json('social.json', $socials);
            echo '{"ok":true}';
            exit;
        }
    }

    // Redirect to avoid re-post
    if ($action !== 'reorder') {
        echo '<script>location.href="/admin/social.php";</script>';
        exit;
    }
}

$socials = load_json('social.json');
usort($socials, fn($a, $b) => ($a['order'] ?? 0) - ($b['order'] ?? 0));

